onSubmit:
const prompt = context.prompt || '';

// Check for common issues
if (prompt.length > 100000) {
  console.warn('[Prompt Validator] Prompt is very long:', prompt.length, 'characters');
}

if (prompt.includes('<api_key>') || prompt.includes('<secret>')) {
  console.warn('[Prompt Validator] Possible API key or secret in prompt!');
}

console.log('[Prompt Validator] Prompt validated successfully');
